import React, {useState} from "react";
import majica from "../slike/majica.png";
import shal from "../slike/shal.png";
import duks from "../slike/duks.png";
import slika from "../slike/pozadina2.jpg";


export default function ProductList() {

    const [selectedSizes, setSelectedSizes] = useState({});

    const products = [
        {
            id: 1,
            name: "Majica Green Army Vitez",
            price: 25,
            image: majica
        },
        {
            id: 2,
            name: "Šal Green Army Vitez",
            price: 15,
            image: shal
        },
        {
            id: 3,
            name: "Duks Green Army Vitez",
            price: 40,
            image: duks
        }
    ];

    const handleSizeChange = (productId, size) => {
        setSelectedSizes(prev => ({
            ...prev,
            [productId]: size
        }));
    };
    const [cart, setCart] = useState([]);

    const handleAddToCart = (product) => {
        const size = selectedSizes[product.id];
        if (!size) {
            alert("Molimo odaberite veličinu.");
            return;
        }

        // Provera da li proizvod sa tom veličinom već postoji u korpi
        const existing = cart.find(item => item.id === product.id && item.size === size);

        if (existing) {
            // Povećaj količinu
            setCart(cart.map(item =>
                item.id === product.id && item.size === size
                    ? { ...item, quantity: item.quantity + 1 }
                    : item
            ));
        } else {
            // Dodaj novi proizvod u korpu
            setCart([...cart, { ...product, quantity: 1, size }]);
        }
    };

    return (
        <div style={{ display: "flex", gap: 20, flexWrap: "wrap" }}>
            {products.map(product => (
                <div key={product.id} style={{ border: "1px solid #ccc", borderRadius: 8, padding: 10, width: 200, textAlign: "center" }}>
                    <img src={product.image} alt={product.name} style={{ width: "100%", height: 180, objectFit: "contain", borderRadius: 8, marginBottom: 10 }} />
                    <h3>{product.name}</h3>
                    <p>{product.price} KM</p>

                    <select
                        value={selectedSizes[product.id] || ""}
                        onChange={(e) => handleSizeChange(product.id, e.target.value)}
                        style={{ marginBottom: 10, width: "100%" }}
                    >
                        <option value="">Odaberi veličinu</option>
                        <option value="S">S</option>
                        <option value="M">M</option>
                        <option value="L">L</option>
                        <option value="XL">XL</option>
                    </select>

                    <button
                        onClick={() => handleAddToCart(product)}
                        style={{ backgroundColor: "#085A48", color: "white", border: "none", padding: "8px 12px", borderRadius: 5, cursor: "pointer", width: "100%" }}
                    >
                        Dodaj u korpu
                    </button>
                </div>
            ))}
        </div>
    );
}
