import { useState } from "react";
import ProductCustomizer from "./components/ProductCustomizer";
import CartSidebar from "./components/CartSidebar";
import "./App.css";

export default function App() {
    const [cart, setCart] = useState([]);
    const [showCart, setShowCart] = useState(false);

    // üì® Slanje narud≈æbe na backend (Mongo Atlas)
    const handleOrderSubmit = async () => {
        if (cart.length === 0) {
            alert("Korpa je prazna!");
            return;
        }

        const ime = prompt("Unesite va≈°e ime:");
        const email = prompt("Unesite va≈° email:");
        const telefon = prompt("Unesite broj telefona:");
        const adresa = prompt("Unesite adresu:");

        const orderData = {
            ime,
            email,
            telefon,
            adresa,
            artikli: cart,
            ukupno: cart.reduce((sum, item) => sum + item.cijena, 0),
        };

        try {
            const res = await fetch("http://localhost:5000/orders", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(orderData),
            });

            const data = await res.json();
            alert(data.poruka || "Narud≈æba poslana!");
            setCart([]);
            setShowCart(false);
        } catch (err) {
            console.error("‚ùå Gre≈°ka prilikom slanja narud≈æbe:", err);
            alert("Gre≈°ka pri slanju narud≈æbe. Poku≈°ajte ponovo.");
        }
    };

    return (
        <div className="App">
            <header className="header">
                <h1>üü¢ KMF Vitez Fan Shop</h1>
                <button className="cart-btn" onClick={() => setShowCart(true)}>
                    üõí Korpa ({cart.length})
                </button>
            </header>

            <main>
                <ProductCustomizer cart={cart} setCart={setCart} />
            </main>

            {showCart && (
                <CartSidebar
                    cart={cart}
                    setCart={setCart}
                    isVisible={showCart}
                    setShowCart={setShowCart}
                />
            )}

            {cart.length > 0 && (
                <div className="submit-section">
                    <button onClick={handleOrderSubmit} className="submit-btn">
                        Po≈°alji narud≈æbu
                    </button>
                </div>
            )}
        </div>
    );
}
