import React, { useState, useEffect } from "react";

export default function OrderForm() {
    const [form, setForm] = useState({ name: "", email: "", phone: "", city: "", message: "" });
    const [cartItems, setCartItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [success, setSuccess] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
        setCartItems(storedCart);
        setTotalPrice(storedCart.reduce((acc, item) => acc + item.price * item.quantity, 0));
    }, []);

    const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = async e => {
        e.preventDefault();
        setError(null);

        if (cartItems.length === 0) {
            setError("Korpa je prazna.");
            return;
        }

        try {
            const res = await fetch(`${process.env.REACT_APP_API_URL}/api/orders`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ ...form, items: cartItems, totalPrice }),
            });

            if (!res.ok) {
                const data = await res.json();
                setError(data.error || "Greška pri slanju narudžbe");
            } else {
                setSuccess(true);
                setForm({ name: "", email: "", phone: "", city: "", message: "" });
                setCartItems([]);
                setTotalPrice(0);
                localStorage.removeItem("cart");
                setTimeout(() => setSuccess(false), 5000);
            }
        } catch {
            setError("Greška u mreži");
        }
    };

    return (
        <section>
            <h1>Naručite</h1>
            {success && <div style={{ color: "green" }}>Narudžba je uspješno poslata!</div>}
            {error && <div style={{ color: "red" }}>{error}</div>}
            <form onSubmit={handleSubmit}>
                <input name="name" placeholder="Ime i prezime" value={form.name} onChange={handleChange} required />
                <input name="email" type="email" placeholder="Email" value={form.email} onChange={handleChange} required />
                <input name="phone" placeholder="Telefon" value={form.phone} onChange={handleChange} required />
                <input name="city" placeholder="Grad" value={form.city} onChange={handleChange} required />
                <textarea name="message" placeholder="Poruka ili napomena" value={form.message} onChange={handleChange} required />
                <button type="submit">Pošalji narudžbu</button>
            </form>
        </section>
    );
}
