import React from "react";

export default function CartSidebar({ cart, setShowCart }) {
    const totalPrice = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);
    const totalItems = cart.reduce((acc, item) => acc + item.quantity, 0);

    return (
        <aside style={{
            position: "fixed",
            top: 0,
            right: 0,
            width: 300,
            height: "100vh",
            backgroundColor: "#f8f8f8",
            padding: 20,
            boxShadow: "-3px 0 10px rgba(0,0,0,0.2)",
            overflowY: "auto",
            zIndex: 1000
        }}>
            <button onClick={() => setShowCart(false)} style={{ marginBottom: 20 }}>Zatvori</button>

            <h2>Korpa ({totalItems} artikala)</h2>

            {cart.length === 0 ? (
                <p>Korpa je prazna.</p>
            ) : (
                cart.map((item, i) => (
                    <div key={i} style={{ marginBottom: 15, borderBottom: "1px solid #ccc", paddingBottom: 10 }}>
                        <p><strong>{item.name}</strong></p>
                        <p>Veličina: {item.size}</p>
                        <p>Količina: {item.quantity}</p>
                        <p>Cijena po komadu: {item.price} KM</p>
                        <p>Ukupno: {item.price * item.quantity} KM</p>
                    </div>
                ))
            )}

            <hr />
            <h3>Ukupno: {totalPrice} KM</h3>
        </aside>
    );
}
