import React, { useState } from "react";
import axios from "axios";

export default function Cart({ cart }) {
    return (
        <div style={{ position: "fixed", right: 0, top: 0, width: "300px", height: "100vh", backgroundColor: "#eee", padding: "20px", overflowY: "auto" }}>
<h2>Korpa</h2>
    {cart.map((item, i) => (
        <div key={i} style={{ marginBottom: "10px", borderBottom: "1px solid #ccc" }}>
            <p><strong>{item.name}</strong></p>
            <p>Veličina: {item.size}</p>
            <p>Količina: {item.quantity}</p>
            <p>Cijena: {item.price} KM</p>
        </div>
    ))}

    <hr />
    <p>
        Ukupno artikala: {cart.reduce((total, item) => total + item.quantity, 0)}
    </p>
    <p>
        Ukupna cijena: {cart.reduce((total, item) => total + item.price * item.quantity, 0)} KM
    </p>
</div>
    );
}
