import React from "react";
import majica from "./slike/majica.png";
import shal from "./slike/shal.png";
import duks from "./slike/duks.png";

export default function App() {
    const products = [
        { id: 1, name: "Majica KMF Vitez", image: majica, price: 25 },
        { id: 2, name: "Šal Zeleni Armija", image: shal, price: 15 },
        { id: 3, name: "Duks KMF", image: duks, price: 40 },
    ];

    return (
        <div style={{ display: "flex", gap: 20, justifyContent: "center", marginTop: 40 }}>
            {products.map((product) => (
                <div
                    key={product.id}
                    style={{
                        border: "1px solid #ccc",
                        borderRadius: 10,
                        padding: 10,
                        width: 180,
                        textAlign: "center",
                        boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
                    }}
                >
                    <img
                        src={product.image}
                        alt={product.name}
                        style={{ width: "100%", borderRadius: 8, marginBottom: 10 }}
                    />
                    <h3>{product.name}</h3>
                    <p>${product.price}</p>
                </div>
            ))}
        </div>
    );
}
