import React, { useState, useEffect } from "react";

export default function OrderForm() {
    const [products, setProducts] = useState([]);
    const [form, setForm] = useState({ name: "", email: "", productId: "" });
    const [success, setSuccess] = useState(false);
    const [loading, setLoading] = useState(true);
    const [fetchError, setFetchError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const res = await fetch(`${process.env.REACT_APP_API_URL}/api/products`);
                if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
                const data = await res.json();
                setProducts(data);
                setLoading(false);
            } catch (err) {
                setFetchError(err.message);
                setLoading(false);
            }
        };
        fetchProducts();
    }, []);

    useEffect(() => {
        if (success) {
            const timer = setTimeout(() => setSuccess(false), 3000);
            return () => clearTimeout(timer);
        }
    }, [success]);

    const handleChange = e => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async e => {
        e.preventDefault();
        try {
            const payload = { ...form, productId: Number(form.productId) };
            const res = await fetch(`${process.env.REACT_APP_API_URL}/api/orders`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(payload)
            });
            if (res.ok) {
                setSuccess(true);
                setForm({ name: "", email: "", productId: "" });
            } else {
                const errData = await res.json();
                alert("Error submitting order: " + (errData.error || res.statusText));
            }
        } catch (err) {
            alert("Network error: " + err.message);
        }
    };

    if (loading) return <p>Loading products...</p>;
    if (fetchError) return <p style={{ color: "red" }}>Error loading products: {fetchError}</p>;

    return (
        <form onSubmit={handleSubmit} style={{ display: "inline-block", textAlign: "left", marginTop: "10px" }}>
            <label>
                Name:<br />
                <input type="text" name="name" value={form.name} onChange={handleChange} required />
            </label><br /><br />
            <label>
                Email:<br />
                <input type="email" name="email" value={form.email} onChange={handleChange} required />
            </label><br /><br />
            <label>
                Product:<br />
                <select name="productId" value={form.productId} onChange={handleChange} required>
                    <option value="">-- Select --</option>
                    {products.map(p => (
                        <option key={p.id} value={p.id}>{p.name}</option>
                    ))}
                </select>
            </label><br /><br />
            <button type="submit">Submit</button>
            {success && <p style={{ color: "green" }}>Order sent!</p>}
        </form>
    );
}
