import React from "react";
import ProductCard from "./ProductCard";

// example: we provide two product variations (home/away images could be same dres-front/back)
export default function ProductGrid({ cart, setCart }) {
    // If you want more products, add them here
    const products = [
        {
            id: "d1",
            name: "Dres 2025/26",
            basePrice: 70,
            front: "/images/dres-front.png",
            back: "/images/dres-back.png"
        }
    ];

    return (
        <div className="product-grid">
            {products.map(p => <ProductCard key={p.id} product={p} cart={cart} setCart={setCart} />)}
        </div>
    );
}
