import React, { useState } from "react";

export default function OrderForm({ cart, clearCart }) {
    const [ime, setIme] = useState("");
    const [email, setEmail] = useState("");
    const [telefon, setTelefon] = useState("");
    const [adresa, setAdresa] = useState("");
    const [loading, setLoading] = useState(false);
    const [msg, setMsg] = useState(null);

    const ukupno = cart.reduce((s,i)=>s + i.cijena * i.kolicina, 0);

    const submitOrder = async (e) => {
        e.preventDefault();
        if (!ime || !email || !telefon || !adresa) { setMsg({type:"error", text:"Popunite sva polja."}); return; }
        if (cart.length === 0) { setMsg({type:"error", text:"Korpa je prazna."}); return; }

        setLoading(true);
        try {
            const resp = await fetch("/orders", {
                method: "POST",
                headers: { "Content-Type":"application/json" },
                body: JSON.stringify({
                    ime, email, telefon, adresa,
                    artikli: cart, ukupno
                })
            });
            const data = await resp.json();
            if (resp.ok) {
                setMsg({type:"success", text:"Narudžba poslana. Hvala!"});
                clearCart();
                setIme(""); setEmail(""); setTelefon(""); setAdresa("");
            } else {
                setMsg({type:"error", text: data.message || "Greška pri slanju."});
            }
        } catch (err) {
            setMsg({type:"error", text:"Greška pri slanju."});
        } finally {
            setLoading(false);
            setTimeout(()=>setMsg(null),8000);
        }
    };

    return (
        <div className="order-form-wrap">
            <h2>Podaci za narudžbu</h2>
            {msg && <div className={`alert ${msg.type}`}>{msg.text}</div>}
            <form className="order-form" onSubmit={submitOrder}>
                <div className="row">
                    <label>Ime i prezime<input value={ime} onChange={e=>setIme(e.target.value)} required/></label>
                    <label>Email<input value={email} onChange={e=>setEmail(e.target.value)} required type="email"/></label>
                </div>

                <div className="row">
                    <label>Telefon<input value={telefon} onChange={e=>setTelefon(e.target.value)} required/></label>
                    <label>Adresa<input value={adresa} onChange={e=>setAdresa(e.target.value)} required/></label>
                </div>

                <div className="summary">
                    <div>Ukupno: <strong>{ukupno} KM</strong></div>
                </div>

                <button type="submit" className="btn-submit" disabled={loading}>
                    {loading ? "Šaljem..." : "Pošalji narudžbu"}
                </button>
            </form>
        </div>
    );
}
