import React from "react";

export default function CartSidebar({ cart, setCart }) {
    const handleRemoveItem = (id, size, customText) => {
        setCart(cart.filter(item => !(item.id === id && item.size === size && item.customText === customText)));
    };

    const handleClearCart = () => {
        if (window.confirm("Jeste li sigurni da ≈æelite oƒçistiti korpu?")) {
            setCart([]);
        }
    };

    const handleOrder = async () => {
        if (cart.length === 0) return alert("Korpa je prazna!");

        try {
            const response = await fetch("http://localhost:5000/orders", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ items: cart }),
            });

            const data = await response.json();
            alert(data.message);
            setCart([]); // oƒçisti korpu nakon slanja
        } catch (err) {
            console.error(err);
            alert("Gre≈°ka pri slanju narud≈æbe.");
        }
    };

    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

    return (
        <div className="fixed top-0 right-0 w-80 bg-white h-full shadow-xl border-l border-gray-200 flex flex-col">
            <div className="flex justify-between items-center p-4 border-b">
                <h2 className="text-lg font-semibold">üõí Korpa</h2>
                <button onClick={handleClearCart} className="text-red-500 hover:text-red-700 text-sm">
                    Oƒçisti korpu
                </button>
            </div>

            <div className="flex-1 overflow-y-auto p-4 space-y-3">
                {cart.length === 0 ? (
                    <p className="text-center text-gray-500">Korpa je prazna</p>
                ) : (
                    cart.map((item, index) => (
                        <div
                            key={index}
                            className="relative border rounded-lg p-3 flex items-center justify-between bg-gray-50 hover:bg-gray-100 transition"
                        >
                            <div>
                                <p className="font-semibold">{item.name}</p>
                                <p className="text-sm text-gray-600">Veliƒçina: {item.size}</p>
                                <p className="text-sm text-gray-600">Koliƒçina: {item.quantity}</p>
                                {item.customText && (
                                    <p className="text-sm font-medium text-green-600">
                                        Natpis: {item.customText}
                                    </p>
                                )}
                                <p className="font-semibold">{item.price * item.quantity} KM</p>
                            </div>

                            <button
                                onClick={() => handleRemoveItem(item.id, item.size, item.customText)}
                                className="absolute top-2 right-2 text-red-500 hover:text-red-700 font-bold"
                            >
                                ‚úï
                            </button>
                        </div>
                    ))
                )}
            </div>

            {cart.length > 0 && (
                <div className="border-t p-4">
                    <div className="flex justify-between mb-3">
                        <span className="font-semibold">Ukupno:</span>
                        <span className="font-semibold">{total} KM</span>
                    </div>
                    <button
                        onClick={handleOrder}
                        className="w-full bg-green-600 text-white py-2 rounded-lg hover:bg-green-700 transition"
                    >
                        Po≈°alji narud≈æbu
                    </button>
                </div>
            )}
        </div>
    );
}
