import React, { useState } from "react";
import ProductGrid from "./components/ProductGrid";
import CartSidebar from "./components/CartSidebar";

function App() {
    const [cart, setCart] = useState([]);
    const [showCart, setShowCart] = useState(false);

    return (
        <div className="min-h-screen bg-gray-100">
            <header className="flex justify-between items-center p-4 bg-green-700 text-white">
                <h1 className="text-xl font-bold">ü¶Å KMF Vitez Fan Shop</h1>
                <button
                    onClick={() => setShowCart(!showCart)}
                    className="bg-white text-green-700 px-4 py-2 rounded-lg font-semibold"
                >
                    Korpa ({cart.length})
                </button>
            </header>

            <main className="p-4">
                <ProductGrid cart={cart} setCart={setCart} />
            </main>

            {showCart && <CartSidebar cart={cart} setCart={setCart} />}
        </div>
    );
}

export default App;
