import React, { useEffect, useState } from "react";
import majica from '../slike/majica.png';
import shal from '../slike/shal.png';
import duks from '../slike/duks.png';
import defaultImg from '../slike/kartice.png';

export default function ProductList() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch(`${process.env.REACT_APP_API_URL}/api/products`)
            .then(res => res.json())
            .then(data => setProducts(data));
    }, []);

    const imageMap = {
        "majica kmf vitez": majica,
        "shal zeleni armija": shal,
        "duks kmf": duks,
    };

    const getProductImage = (product) => {
        const key = product.name.toLowerCase().trim();
        return imageMap[key] || defaultImg;
    };

    return (
        <div className="product-container">
            <div className="grid">
                {products.map(product => {
                    console.log("PROIZVOD:", product.name); // debug
                    return (
                        <div key={product.id} className="grid-kartice">
                            <img
                                src={getProductImage(product)}
                                alt={product.name}
                                className="product-image"
                            />
                            <h3>{product.name}</h3>
                            <p>${product.price}</p>
                        </div>
                    );
                })}
            </div>
        </div>
    );
}
