import React, { useState } from "react";
import front from "../slike/dres-front.png";
import back from "../slike/dres-back.png";

export default function ProductGrid({ cart, setCart }) {
    const [selectedSizes, setSelectedSizes] = useState({});
    const [hovered, setHovered] = useState(null);

    const products = [
        {
            id: 1,
            name: "Dres Green Army Vitez",
            price: 60,
            front,
            back,
        },
    ];

    const handleAddToCart = (product) => {
        const size = selectedSizes[product.id];
        if (!size) return alert("Odaberite veličinu!");
        const existing = cart.find((i) => i.id === product.id && i.size === size);
        if (existing) {
            setCart(
                cart.map((i) =>
                    i.id === product.id && i.size === size
                        ? { ...i, quantity: i.quantity + 1 }
                        : i
                )
            );
        } else {
            setCart([...cart, { ...product, size, quantity: 1 }]);
        }
    };

    return (
        <div className="product-grid">
            {products.map((p) => (
                <div
                    className="product-card"
                    key={p.id}
                    onMouseEnter={() => setHovered(p.id)}
                    onMouseLeave={() => setHovered(null)}
                >
                    <img
                        src={hovered === p.id ? p.back : p.front}
                        alt={p.name}
                        className="product-image"
                    />
                    <h3>{p.name}</h3>
                    <p>{p.price} KM</p>
                    <select
                        value={selectedSizes[p.id] || ""}
                        onChange={(e) =>
                            setSelectedSizes({ ...selectedSizes, [p.id]: e.target.value })
                        }
                    >
                        <option value="">Odaberi veličinu</option>
                        <option value="S">S</option>
                        <option value="M">M</option>
                        <option value="L">L</option>
                        <option value="XL">XL</option>
                    </select>
                    <button onClick={() => handleAddToCart(p)}>Dodaj u korpu</button>
                </div>
            ))}
        </div>
    );
}
