import {useState} from "react";

export default function ProductList({ cart, setCart }) {
    const [selectedSizes, setSelectedSizes] = useState([]);
    const products = [
        { id: 1, name: "Majica Green Army Vitez", price: 25 },
        { id: 2, name: "Šal Green Army Vitez", price: 15 },
        { id: 3, name: "Duks Green Army Vitez", price: 40 },
    ];

    const handleAddToCart = (product) => {
        const size = selectedSizes[product.id];
        if (!size) return alert("Odaberite veličinu");

        const exist = cart.find((i) => i.id === product.id && i.size === size);
        if (exist) {
            setCart(
                cart.map((i) =>
                    i.id === product.id && i.size === size
                        ? { ...i, quantity: i.quantity + 1 }
                        : i
                )
            );
        } else {
            setCart([...cart, { ...product, size, quantity: 1 }]);
        }
    };

    return (
        <div>
            {products.map((product) => (
                <div key={product.id}>
                    <h3>{product.name}</h3>
                    <p>{product.price} KM</p>
                    <select
                        value={selectedSizes[product.id] || ""}
                        onChange={(e) =>
                            setSelectedSizes({ ...selectedSizes, [product.id]: e.target.value })
                        }
                    >
                        <option value="">Odaberi veličinu</option>
                        <option value="S">S</option>
                        <option value="M">M</option>
                        <option value="L">L</option>
                        <option value="XL">XL</option>
                    </select>
                    <button onClick={() => handleAddToCart(product)}>Dodaj u korpu</button>
                </div>
            ))}
        </div>
    );
}
