import { useState, useEffect } from "react";
import ProductList from "./components/ProductList";
import OrderForm from "./components/OrderForm";
import '../src/assets/styles.css'
import logo from '../src/slike/logonacijaca.png'
import Footer from "./components/Footer";
import Header from "./components/Header";

export default function App() {
    const [loading, setLoading] = useState(true);
    const [cart, setCart] = useState([]);
    const [showCart, setShowCart] = useState(false);

    useEffect(() => {
        const timer = setTimeout(() => {
            setLoading(false);
        }, 2500);
        return () => clearTimeout(timer);
    }, []);

    if (loading) {
        return (
            <div className="loading-screen">
                <div className="loading-text">KMF VITEZ - GREEN ARMY VITEZ</div>
                <br />
                <div className="loading-image">
                    <img src={logo} alt="Logo" />
                    <br />
                </div>
                <br />
            </div>
        );
    }

    return (
        <div className="app-content fade-in">
            <Header cart={cart} showCart={showCart} setShowCart={setShowCart} />

            <div style={{ fontFamily: "Arial, sans-serif", textAlign: "center" }}>
                <header>
                    <h1>KMF VITEZ</h1>
                    <h2>Green Army Vitez Fan Shop</h2>
                    <p className="pisani-text-header">#Budi dio priče, budi VITEZ!</p>
                </header>

                {/* Proslijedi cart i setCart u ProductList da možeš dodavati u korpu */}
                <ProductList cart={cart} setCart={setCart} />

                <section style={{ marginTop: "40px" }}>
                    <h2>Naruči!</h2>
                    <OrderForm cart={cart} />
                </section>
                <br />
                <Footer />
            </div>
        </div>
    );
}
