import { useState, useEffect } from "react";
import ProductList from "./components/ProductList";
import OrderForm from "./components/OrderForm";
import Footer from "./components/Footer";
import Header from "./components/Header";
import logo from './slike/logonacijaca.png';
import CartSidebar from "./components/CartSidebar";
import TopBanner from "./components/TopBanner";
import "./App.css";

export default function App() {

    const [cart, setCart] = useState([]);
    const [showCart, setShowCart] = useState(false);

    useEffect(() => {
        const storedCart = localStorage.getItem("cart");
        if (storedCart) setCart(JSON.parse(storedCart));
    }, []);

    useEffect(() => {
        localStorage.setItem("cart", JSON.stringify(cart));
    }, [cart]);

    return (
        <div className="app-content">
            <TopBanner />
            <Header cart={cart} showCart={showCart} setShowCart={setShowCart} />
            <CartSidebar cart={cart} setCart={setCart} isVisible={showCart} setShowCart={setShowCart} />

            <main>
                <ProductList cart={cart} setCart={setCart} />
                <section id="order">
                    <OrderForm cart={cart} setCart={setCart} />
                </section>

            </main>

            <Footer />
        </div>

    );

}
