import React from "react";
import majica from "../slike/majica.png";
import shal from "../slike/shal.png";
import duks from "../slike/duks.png";
import slika from "../slike/gav8.jpg"

export default function ProductList() {
    const products = [
        {
            id: 1,
            name: "Majica Green Army Vitez",
            price: 25,
            image: majica
        },
        {
            id: 2,
            name: "Šal Green Army Vitez",
            price: 15,
            image: shal
        },
        {
            id: 3,
            name: "Duks Green Army Vitez",
            price: 40,
            image: duks
        }
    ];

    return (
        <div className="product-container">
            <div className="grid">
                <div style={{ position: "relative", padding: "40px", overflow: "hidden" }}/>
                    {/* Pozadinske slike */}
                    <img
                        src="../slike/gav8.jpg"
                        alt="bg1"
                        style={{
                            position: "absolute",
                            top: "10%",
                            left: "5%",
                            width: "250px",
                            opacity: 0.08,
                            zIndex: 0,
                        }}
                    />
                    <img
                        src="../slike/gav0.jpg"
                        alt="bg2"
                        style={{
                            position: "absolute",
                            bottom: "10%",
                            right: "5%",
                            width: "250px",
                            opacity: 0.08,
                            zIndex: 0,
                        }}
                    />
                {products.map(product => (
                    <div key={product.id} className="grid-kartice" style={{border: "1px solid #ccc", borderRadius: "10px",
                        padding: "10px", width: "200px", textAlign: "center", boxShadow: "0 0 8px rgba(0,0,0,0.1)"
                        }}>
                        <img src={product.image} alt={product.name} style={{
                                width: "100%",
                                height: "180px",
                                objectFit: "contain",
                                borderRadius: "8px",
                                marginBottom: "10px"
                            }}
                        />
                        <h3>{product.name}</h3>
                        <p>{product.price}KM</p>
                    </div>
                ))}
            </div>

        </div>
    );
}
