import { useState } from "react";
import axios from "axios";

export default function OrderForm({ cart, setCart }) {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        brojTelefona: "",
        grad: "",
        poruka: "",
    });

    const totalPrice = cart.reduce((acc, item) => acc + item.price, 0);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (cart.length === 0) return alert("Korpa je prazna!");

        try {
            await axios.post(`https://tvoj-backend-link.onrender.com/api/orders`, {
                items: cart,
                total: totalPrice,
                ...formData,
            });

            setCart([]);
            localStorage.removeItem("cart");
            alert("Narudžba uspješno poslana!");
        } catch (err) {
            console.error(err);
            alert("Greška prilikom slanja narudžbe.");
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Pošalji narudžbu</h2>
            <input name="name" placeholder="Ime i prezime" onChange={handleChange} required />
            <input name="email" type="email" placeholder="Email" onChange={handleChange} required />
            <input name="brojTelefona" placeholder="Broj telefona" onChange={handleChange} required />
            <input name="grad" placeholder="Grad" onChange={handleChange} required />
            <textarea name="poruka" placeholder="Poruka (opcionalno)" onChange={handleChange} />
            <button type="submit">Pošalji narudžbu</button>
        </form>
    );
}
