export default function CartSidebar({ cart, setCart, isVisible, setShowCart }) {
    const handleRemove = (index) => {
        const updatedCart = [...cart];
        updatedCart.splice(index, 1);
        setCart(updatedCart);
    };

    return (
        <div className={`cart-sidebar ${isVisible ? "show" : ""}`}>
            <h2>Korpa</h2>
            {cart.length === 0 ? (
                <p>Korpa je prazna</p>
            ) : (
                <ul>
                    {cart.map((item, index) => (
                        <li key={index} style={{ marginBottom: "10px" }}>
                            <div>{item.name} - {item.size} - {item.quantity} kom - {item.price*item.quantity} KM</div>
                            {item.customText && <div>Natpis: {item.customText}</div>}
                            <button onClick={() => handleRemove(index)} style={{ marginTop: "5px", backgroundColor: "#d9534f", color: "#fff", border: "none", padding: "5px 10px", borderRadius: "5px" }}>
                                Obri≈°i
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}
