import React from "react";

export default function CartSidebar({ cart, setCart, isVisible, setShowCart }) {
    const handleRemove = (index) => {
        const newCart = [...cart];
        newCart.splice(index, 1);
        setCart(newCart);
    };

    return (
        <>
            {isVisible && <div className="overlay" onClick={() => setShowCart(false)}></div>}
            <div className={`cart-sidebar ${isVisible ? "show" : ""}`}>
                <button onClick={() => setShowCart(false)}>✖ Zatvori</button>

                {cart.length === 0 ? (
                    <p>Korpa je prazna</p>
                ) : (
                    <table>
                        <thead>
                        <tr>
                            <th>Proizvod</th>
                            <th>Varijanta</th>
                            <th>Natpis</th>
                            <th>Veličina</th>
                            <th>Količina</th>
                            <th>Cijena</th>
                            <th>Ukloni</th>
                        </tr>
                        </thead>
                        <tbody>
                        {cart.map((item, idx) => (
                            <tr key={idx}>
                                <td>{item.name}</td>
                                <td>{item.variant === "home" ? "Domaći" : "Gostujući"}</td>
                                <td>{item.customText ? "Sa natpisom" : "Bez natpisa"}</td>
                                <td>{item.size}</td>
                                <td>{item.quantity}</td>
                                <td>{item.price * item.quantity} KM</td>
                                <td>
                                    <button onClick={() => handleRemove(idx)}>❌</button>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                )}
            </div>
        </>
    );
}
