import React, { useState } from "react";
import bijelaMajica from "../slike/Bpolo.jpg";
import crnaMajica from "../slike/Cpolo.jpg";
import navijackaCrna from "../slike/Cmajica.jpg";
import trenerkaZelena from "../slike/Ztrenerka.jpg";
import trenerkaCrna from "../slike/Ctrenerka.jpg";
import dresZeleniDomaci from "../slike/Zdres.jpg";
import dresZeleniGosti from "../slike/Bdres.jpg";


export default function ProductGrid({ cart, setCart }) {
    const [hovered, setHovered] = useState(null);
    const [selectedSizes, setSelectedSizes] = useState({});
    const [customText, setCustomText] = useState({});
    const [textOption, setTextOption] = useState({}); // 1=bez, 2=igrač, 3=lično
    const players = ["Igrač 1", "Igrač 2", "Igrač 3"]; // možeš dodati prave igrače


    const products = [
        { id: 1, name: "Polo majica bijela", price: 20, front: bijelaMajica, back: bijelaMajica }, // nema druge strane
        { id: 2, name: "Polo majica crna", price: 20, front: crnaMajica, back: crnaMajica },
        { id: 3, name: "Navijačka majica crna", price: 25, front: navijackaCrna, back: navijackaCrna },
        { id: 4, name: "Trenerka zelena", price: 50, front: trenerkaZelena, back: trenerkaZelena },
        { id: 5, name: "Trenerka crna", price: 50, front: trenerkaCrna, back: trenerkaCrna },
        { id: 6, name: "Dres zeleni domaći", price: 40, front: dresZeleniDomaci, back: dresZeleniDomaci },
        { id: 7, name: "Dres zeleni gostujući", price: 40, front: dresZeleniGosti, back: dresZeleniGosti },
    ];
    const handleAddToCart = (product) => {
        const size = selectedSizes[product.id];
        if (!size) {
            alert("Molimo odaberite veličinu.");
            return;
        }

        const text = customText[product.id] || "";

        const existing = cart.find(
            (item) =>
                item.id === product.id &&
                item.size === size &&
                item.customText === text
        );

        if (existing) {
            setCart(
                cart.map((item) =>
                    item.id === product.id &&
                    item.size === size &&
                    item.customText === text
                        ? { ...item, quantity: item.quantity + 1 }
                        : item
                )
            );
        } else {
            setCart([
                ...cart,
                { ...product, size, quantity: 1, customText: text },
            ]);
        }
    };

    return (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 p-4">
            {products.map((p) => (
                <div
                    key={p.id}
                    className="relative bg-white rounded-2xl shadow-md p-4 text-center hover:shadow-lg transition"
                    onMouseEnter={() => setHovered(p.id)}
                    onMouseLeave={() => setHovered(null)}
                >
                    <div className="relative">
                        <img
                            src={hovered === p.id ? p.back : p.front}
                            alt={p.name}
                            className="rounded-xl w-full h-72 object-cover"
                        />
                        {customText[p.id] && (
                            <div className="absolute bottom-10 left-1/2 transform -translate-x-1/2 text-white text-xl font-bold drop-shadow-lg">
                                {customText[p.id]}
                            </div>
                        )}
                    </div>

                    <h3 className="font-semibold mt-3 text-lg">{p.name}</h3>
                    <p className="text-green-600 font-bold mb-2">{p.price} KM</p>

                    <select
                        value={selectedSizes[p.id] || ""}
                        onChange={(e) =>
                            setSelectedSizes({
                                ...selectedSizes,
                                [p.id]: e.target.value,
                            })
                        }
                        className="border rounded-lg p-2 w-full mb-2"
                    >
                        <option value="">Odaberi veličinu</option>
                        <option value="S">S</option>
                        <option value="M">M</option>
                        <option value="L">L</option>
                        <option value="XL">XL</option>
                    </select>

                    {/* Opcije natpisa */}
                    <div className="mb-3">
                        <select
                            value={textOption[p.id] || "none"}
                            onChange={(e) => {
                                const val = e.target.value;
                                setTextOption({ ...textOption, [p.id]: val });
                                setCustomText({ ...customText, [p.id]: "" });
                            }}
                            className="border rounded-lg p-2 w-full"
                        >
                            <option value="none">Bez natpisa</option>
                            <option value="player">Izaberi igrača</option>
                            <option value="personal">Lično (upiši ime/broj)</option>
                        </select>

                        {/* Ako izabere igrača */}
                        {textOption[p.id] === "player" && (
                            <select
                                value={customText[p.id] || ""}
                                onChange={(e) =>
                                    setCustomText({
                                        ...customText,
                                        [p.id]: e.target.value,
                                    })
                                }
                                className="border rounded-lg p-2 w-full mt-2"
                            >
                                <option value="">Odaberi igrača</option>
                                {players.map((pl, idx) => (
                                    <option key={idx} value={pl}>
                                        {pl}
                                    </option>
                                ))}
                            </select>
                        )}

                        {/* Ako izabere lični natpis */}
                        {textOption[p.id] === "personal" && (
                            <input
                                type="text"
                                placeholder="Unesite ime ili broj"
                                value={customText[p.id] || ""}
                                onChange={(e) =>
                                    setCustomText({
                                        ...customText,
                                        [p.id]: e.target.value,
                                    })
                                }
                                className="border rounded-lg p-2 w-full mt-2"
                            />
                        )}
                    </div>

                    <button
                        onClick={() => handleAddToCart(p)}
                        className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition w-full"
                    >
                        Dodaj u korpu
                    </button>
                </div>
            ))}
        </div>
    );
}
