import { useState, useEffect } from "react";
import ProductList from "./components/ProductList";
import OrderForm from "./components/OrderForm";
import '../src/assets/styles.css'


export default function App() {

    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const timer = setTimeout(() => {
            setLoading(false);
        }, 1500); // 2.5 sekunde loading
        return () => clearTimeout(timer);
    }, []);

    if (loading) {
        return (
            <div className="loading-screen">
                <div className="loading-text">KMF VITEZ</div>
                <br/>
                <div className="loading-text">GREEN ARMY VITEZ</div>
                <div className="loading-image">
                    <img src="slike/logonacijaca.png"></img>
                </div>
            </div>
        );
    }
    return (
        <div style={{ fontFamily: "Arial, sans-serif", textAlign: "center" }}>
            <header style={{ backgroundColor: "#0a8b46", color: "white", padding: "20px" }}>
                <h1>KMF VITEZ</h1>
                <h2>Green Army Vitez Fan Shop</h2>
                <p>#Budi dio priƒçe, budi VITEZ!</p>
            </header>

            <ProductList />

            <section style={{ marginTop: "40px" }}>
                <h2>Place an Order</h2>
                <OrderForm />
            </section>
        </div>
    );
}
