import React from "react";

export default function ProductList() {
    const products = [
        {
            id: 1,
            name: "Majica KMF Vitez",
            price: 25,
            image: "/images/majica.png"
        },
        {
            id: 2,
            name: "Šal Zeleni Armija",
            price: 15,
            image: "/images/shal.png"
        },
        {
            id: 3,
            name: "Duks KMF",
            price: 40,
            image: "/images/duks.png"
        }
    ];

    return (
        <div style={{ display: "flex", gap: "20px", padding: "40px", justifyContent: "center" }}>
            {products.map(product => (
                <div
                    key={product.id}
                    style={{
                        border: "1px solid #ccc",
                        borderRadius: "10px",
                        padding: "10px",
                        width: "200px",
                        textAlign: "center",
                        boxShadow: "0 2px 8px rgba(0,0,0,0.1)"
                    }}
                >
                    <img
                        src={product.image}
                        alt={product.name}
                        style={{
                            width: "100%",
                            height: "180px",
                            objectFit: "cover",
                            borderRadius: "8px",
                            marginBottom: "10px"
                        }}
                    />
                    <h3>{product.name}</h3>
                    <p>${product.price}</p>
                </div>
            ))}
        </div>
    );
}
