import React from "react";

export default function CartSidebar({ cart, setShowCart }) {
    const totalPrice = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);

    return (
        <aside style={{
            position: "fixed",
            top: 0,
            right: 0,
            width: "300px",
            height: "100vh",
            backgroundColor: "#f5f5f5",
            padding: "20px",
            boxShadow: "-2px 0 5px rgba(0,0,0,0.2)",
            overflowY: "auto",
            zIndex: 1000
        }}>
            <button onClick={() => setShowCart(false)} style={{ marginBottom: "20px" }}>Zatvori</button>
            <h2>Korpa ({totalItems} artikala)</h2>
            {cart.length === 0 ? (
                <p>Korpa je prazna</p>
            ) : (
                cart.map((item, index) => (
                    <div key={index} style={{ marginBottom: "15px", borderBottom: "1px solid #ccc", paddingBottom: "10px" }}>
                        <p><strong>{item.name}</strong></p>
                        <p>Veličina: {item.size}</p>
                        <p>Količina: {item.quantity}</p>
                        <p>Cijena po komadu: {item.price} KM</p>
                        <p>Ukupno: {item.price * item.quantity} KM</p>
                    </div>
                ))
            )}
            <hr />
            <h3>Ukupno: {totalPrice} KM</h3>
        </aside>
    );
}
