import React from "react";
import logokmf from "../slike/kmflogo.png";
import logogav from "../slike/logogav.png";

export default function TopBar({ cart, showCart, setShowCart }) {
    const totalItems = cart.reduce((acc, item) => acc + item.quantity, 0);

    return (
        <div style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            backgroundColor: "white",
            padding: "10px 20px",
            borderBottom: "1px solid #ccc",
            fontFamily: "Arial, sans-serif"
        }}>
            <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
                <img src={logokmf} alt="logo1" style={{ height: "50px" }} />
                <div>
                    <h1 style={{
                        margin: 0,
                        fontWeight: "bold",
                        fontSize: "28px",
                        color: "#085a48"
                    }}>KMF Vitez</h1>
                    <p style={{ margin: "5px 0 0", fontSize: "14px" }}>Green Army Vitez</p>
                </div>
                <img src={logogav} alt="logo2" style={{ height: "60px" }} />
            </div>

            <button
                onClick={() => setShowCart(!showCart)}
                style={{
                    backgroundColor: "#085A48",
                    color: "white",
                    padding: "8px 16px",
                    borderRadius: "5px",
                    cursor: "pointer",
                    border: "none",
                    fontWeight: "bold"
                }}
            >
                Korpa ({totalItems})
            </button>
        </div>
    );
}
