import React from "react";

export default function Header({ cart, showCart, setShowCart }) {
    // Ukupan broj artikala u korpi
    const totalItems = cart.reduce((acc, item) => acc + item.quantity, 0);

    return (
        <header style={{ padding: 20,
            backgroundColor: "white",
            borderBottom: "3px solid #085A48",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            flexWrap: "wrap" }}>
            <div>
                <h1 style={{ color: "#085A48", margin: 0 }}>KMF Vitez</h1>
                <p style={{ margin: "4px 0", fontStyle: "italic", fontWeight: "500" }}>green army</p>
                <h2 style={{ margin: "8px 0 0", fontSize: "20px" }}>Green Army Vitez</h2>
                <p style={{ margin: "4px 0", fontSize: "14px" }}>Official KMF Vitez Fan Shop</p>
                <p style={{ margin: 0, fontSize: "14px" }}>
                    Support your team with official merchandise. High-quality products for true fans.
                </p>
            </div>

            <button
                onClick={() => setShowCart(!showCart)}
                style={{
                    backgroundColor: "#085A48",
                    color: "white",
                    padding: "8px 16px",
                    borderRadius: "5px",
                    cursor: "pointer",
                    border: "none",
                    fontWeight: "bold"
                }}
            >
                Korpa ({totalItems})
            </button>
        </header>
    );
}
