import React, { useState } from "react";
import axios from "axios";

export default function OrderForm({ cart, setCart }) {
    const [form, setForm] = useState({ name: "", email: "", phone: "", city: "", message: "" });
    const [success, setSuccess] = useState(false);
    const [error, setError] = useState(null);

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (cart.length === 0) return alert("Korpa je prazna.");

        try {
            const totalPrice = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);
            await axios.post("http://localhost:5000/orders", { ...form, items: cart, totalPrice });
            setCart([]);
            setForm({ name: "", email: "", phone: "", city: "", message: "" });
            localStorage.removeItem("cart");
            setSuccess(true);
            setError(null);
            setTimeout(() => setSuccess(false), 5000);
        } catch (err) {
            setError("Greška prilikom slanja narudžbe.");
        }
    };

    return (
        <div className="form-container">
            {success && <div className="alert success">Narudžba uspješno poslana!</div>}
            {error && <div className="alert error">{error}</div>}
            <h2>Naručite</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-row">
                    <label>Ime i prezime<input type="text" name="name" value={form.name} onChange={handleChange} required /></label>
                    <label>Email<input type="email" name="email" value={form.email} onChange={handleChange} required /></label>
                </div>
                <div className="form-row">
                    <label>Telefon<input type="tel" name="phone" value={form.phone} onChange={handleChange} required /></label>
                    <label>Grad<input type="text" name="city" value={form.city} onChange={handleChange} required /></label>
                </div>
                <label>Poruka<textarea name="message" value={form.message} onChange={handleChange} /></label>
                <button type="submit">Pošalji</button>
            </form>
        </div>
    );
}
