import React from "react";

export default function CartSidebar({ cart, setCart, isVisible, setShowCart }) {
    const totalPrice = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

    const removeItem = (index) => {
        const newCart = [...cart];
        newCart.splice(index, 1);
        setCart(newCart);
    };

    return (
        <div className={`cart-sidebar ${isVisible ? "show" : ""}`}>
            <h2>Korpa</h2>
            {cart.length === 0 ? (
                <p>Korpa je prazna</p>
            ) : (
                <table>
                    <thead>
                    <tr>
                        <th>Proizvod</th>
                        <th>Veličina</th>
                        <th>Količina</th>
                        <th>Cijena</th>
                        <th>Ukloni</th>
                    </tr>
                    </thead>
                    <tbody>
                    {cart.map((item, index) => (
                        <tr key={index}>
                            <td>{item.name}</td>
                            <td>{item.size}</td>
                            <td>{item.quantity}</td>
                            <td>{item.price * item.quantity} KM</td>
                            <td>
                                <button onClick={() => removeItem(index)}>X</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
            <h3>Ukupno: {totalPrice} KM</h3>
        </div>
    );
}
