import React from 'react';

export default function Header({ cart, showCart, setShowCart }) {
    const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);

    // Ukupna cijena
    const totalPrice = cart.reduce((sum, item) => sum + item.quantity * item.price, 0);
    return (
        <header style={{ padding: "10px", borderBottom: "2px solid #085A48", backgroundColor: "white" }}>
            <h1 style={{ color: "#085A48" }}>KMF Vitez</h1>
            <p>Green Army Vitez</p>
            <button onClick={() => setShowCart(!showCart)}>
                {showCart ? "Zatvori korpu" : "Otvori korpu"} ({totalItems})
            </button>
        </header>
    );
}
