import React, { useState } from "react";
import dresZeleniDomaci from "../slike/Zdres.jpg";
import dresZeleniGosti from "../slike/Bdres.jpeg";

export default function ProductGrid({ cart, setCart }) {
    const [hovered, setHovered] = useState(null);
    const [selectedSizes, setSelectedSizes] = useState({});
    const [customText, setCustomText] = useState({});
    const [textOption, setTextOption] = useState({});
    const players = ["Markoviƒá 10", "Juriƒá 7", "Kovaƒçeviƒá 9"];

    const products = [
        { id: 1, name: "Dres zeleni domaƒái", price: 40, front: dresZeleniDomaci, back: dresZeleniDomaci },
        { id: 2, name: "Dres zeleni gostujuƒái", price: 40, front: dresZeleniGosti, back: dresZeleniGosti },
    ];

    const handleAddToCart = (product) => {
        const selectedSize = selectedSizes[product.id];
        const text = customText[product.id] || "";
        if (!selectedSize) {
            alert("Molimo odaberite veliƒçinu.");
            return;
        }

        const existing = cart.find(
            (item) =>
                item.id === product.id &&
                item.size === selectedSize &&
                item.customText === text
        );

        if (existing) {
            setCart(
                cart.map((item) =>
                    item.id === product.id &&
                    item.size === selectedSize &&
                    item.customText === text
                        ? { ...item, quantity: item.quantity + 1 }
                        : item
                )
            );
        } else {
            setCart([
                ...cart,
                { ...product, size: selectedSize, quantity: 1, customText: text },
            ]);
        }
    };

    const sizes = ["S", "M", "L", "XL"];

    return (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 p-6">
            {products.map((p) => (
                <div
                    key={p.id}
                    className="bg-white border rounded-2xl shadow-lg overflow-hidden hover:shadow-2xl transition duration-300"
                    onMouseEnter={() => setHovered(p.id)}
                    onMouseLeave={() => setHovered(null)}
                >
                    <div className="relative">
                        <img
                            src={hovered === p.id ? p.back : p.front}
                            alt={p.name}
                            className="w-full h-80 object-cover transition-transform duration-500"
                        />
                        {customText[p.id] && (
                            <div className="absolute bottom-10 left-1/2 transform -translate-x-1/2 text-white text-2xl font-bold tracking-wide drop-shadow-md uppercase">
                                {customText[p.id]}
                            </div>
                        )}
                    </div>

                    <div className="p-5 text-center">
                        <h3 className="text-lg font-semibold mb-1">{p.name}</h3>
                        <p className="text-green-700 font-bold mb-3">{p.price} KM</p>

                        {/* Odabir veliƒçine */}
                        <div className="flex justify-center gap-2 mb-3">
                            {sizes.map((s) => (
                                <button
                                    key={s}
                                    onClick={() =>
                                        setSelectedSizes({
                                            ...selectedSizes,
                                            [p.id]: s,
                                        })
                                    }
                                    className={`px-4 py-1 rounded-lg border transition ${
                                        selectedSizes[p.id] === s
                                            ? "bg-green-600 text-white border-green-600"
                                            : "border-gray-400 hover:bg-green-100"
                                    }`}
                                >
                                    {s}
                                </button>
                            ))}
                        </div>

                        {/* Odabir natpisa */}
                        <select
                            value={textOption[p.id] || "none"}
                            onChange={(e) => {
                                const val = e.target.value;
                                setTextOption({ ...textOption, [p.id]: val });
                                setCustomText({ ...customText, [p.id]: "" });
                            }}
                            className="border p-2 rounded-lg w-full mb-2"
                        >
                            <option value="none">Bez natpisa</option>
                            <option value="player">Izaberi igraƒça</option>
                            <option value="personal">Liƒçno (upi≈°i ime/broj)</option>
                        </select>

                        {textOption[p.id] === "player" && (
                            <select
                                value={customText[p.id] || ""}
                                onChange={(e) =>
                                    setCustomText({
                                        ...customText,
                                        [p.id]: e.target.value,
                                    })
                                }
                                className="border p-2 rounded-lg w-full mb-3"
                            >
                                <option value="">Odaberi igraƒça</option>
                                {players.map((pl, idx) => (
                                    <option key={idx} value={pl}>
                                        {pl}
                                    </option>
                                ))}
                            </select>
                        )}

                        {textOption[p.id] === "personal" && (
                            <input
                                type="text"
                                placeholder="Unesite ime ili broj"
                                value={customText[p.id] || ""}
                                onChange={(e) =>
                                    setCustomText({
                                        ...customText,
                                        [p.id]: e.target.value,
                                    })
                                }
                                className="border p-2 rounded-lg w-full mb-3"
                            />
                        )}

                        <button
                            onClick={() => handleAddToCart(p)}
                            className="bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-5 rounded-lg w-full flex justify-center items-center gap-2 transition"
                        >
                            üõí Dodaj u korpu
                        </button>
                    </div>
                </div>
            ))}
        </div>
    );
}
