import React, { useState, useEffect } from "react";

export default function ProductList({ cart, setCart }) {
    const [selectedSizes, setSelectedSizes] = useState({});

    const products = [
        { id: 1, name: "Majica Green Army Vitez", price: 25 },
        { id: 2, name: "Šal Green Army Vitez", price: 15 },
        { id: 3, name: "Duks Green Army Vitez", price: 40 },
    ];

    useEffect(() => {
        localStorage.setItem("cart", JSON.stringify(cart));
    }, [cart]);

    const handleSizeChange = (productId, size) => {
        setSelectedSizes(prev => ({ ...prev, [productId]: size }));
    };

    const handleAddToCart = (product) => {
        const size = selectedSizes[product.id];
        if (!size) {
            alert("Molimo odaberite veličinu");
            return;
        }

        const existing = cart.find(item => item.id === product.id && item.size === size);

        if (existing) {
            setCart(cart.map(item =>
                item.id === product.id && item.size === size
                    ? { ...item, quantity: item.quantity + 1 }
                    : item
            ));
        } else {
            setCart([...cart, { ...product, size, quantity: 1 }]);
        }
    };

    return (
        <div>
            {products.map(product => (
                <div key={product.id} style={{border: "1px solid #ccc", margin: 10, padding: 10}}>
                    <h3>{product.name}</h3>
                    <p>Cijena: {product.price} KM</p>
                    <select value={selectedSizes[product.id] || ""} onChange={e => handleSizeChange(product.id, e.target.value)}>
                        <option value="">Odaberi veličinu</option>
                        <option value="S">S</option>
                        <option value="M">M</option>
                        <option value="L">L</option>
                        <option value="XL">XL</option>
                    </select>
                    <button onClick={() => handleAddToCart(product)}>Dodaj u korpu</button>
                </div>
            ))}
            <div>
                <h4>Korpa: {cart.reduce((acc, i) => acc + i.quantity, 0)} artikala</h4>
                <p>Ukupno: {cart.reduce((acc, i) => acc + i.price * i.quantity, 0)} KM</p>
            </div>
        </div>
    );
}
