import React, { useEffect, useState } from "react";
import majica from '../slike/majica.png';
import shal from '../slike/shal.png';
import duks from '../slike/duks.png';

export default function ProductList() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch(`${process.env.REACT_APP_API_URL}/api/products`)
            .then(res => res.json())
            .then(data => setProducts(data));
    }, []);

    const imageMap = {
        "majica kmf vitez": majica,
        "sal zeleni armija": shal,
        "kapa vitez": duks,
    };

    const getProductImage = (product) => {
        const key = product.name
            .toLowerCase()
            .normalize("NFD")
            .replace(/[\u0300-\u036f]/g, "")
            .trim();
        return imageMap[key] || null; // vrati null ako nema slike
    };

    return (
        <div className="product-container">
            <div className="grid">
                {products.map(product => {
                    const imageSrc = getProductImage(product);
                    return (
                        <div key={product.id} className="grid-kartice">
                            {imageSrc && (
                                <img
                                    src={imageSrc}
                                    alt={product.name}
                                    className="product-image"
                                />
                            )}
                            <h3>{product.name}</h3>
                            <p>${product.price}</p>
                        </div>
                    );
                })}
            </div>
        </div>
    );
}
