import React, { useState, useEffect } from "react";

export default function OrderForm() {
    const [form, setForm] = useState({ name: "", email: "", phone: "", message: "", city: "" });
    const [cartItems, setCartItems] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [success, setSuccess] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
        setCartItems(storedCart);
        const total = storedCart.reduce((sum, item) => sum + item.price * item.quantity, 0);
        setTotalPrice(total);
    }, []);

    const handleChange = e => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async e => {
        e.preventDefault();
        setError(null);

        try {
            const res = await fetch(`${process.env.REACT_APP_API_URL}/api/orders`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ ...form, items: cartItems, totalPrice }),
            });

            if (!res.ok) {
                const data = await res.json();
                throw new Error(data.error || "Greška prilikom slanja.");
            }

            setSuccess(true);
            setForm({ name: "", email: "", phone: "", message: "", city: "" });
            setCartItems([]);
            setTotalPrice(0);
            localStorage.removeItem("cart");

            setTimeout(() => setSuccess(false), 8000);
        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <section className="form-section" id="order">
            <div className="form-container">
                {success && <div className="alert success">Uspješno ste naručili!</div>}
                {error && <div className="alert error">{error}</div>}
                <h1>Naručite</h1>
                <form onSubmit={handleSubmit}>
                    <input type="text" name="name" value={form.name} onChange={handleChange} placeholder="Ime i prezime" required />
                    <input type="email" name="email" value={form.email} onChange={handleChange} placeholder="Email" required />
                    <input type="tel" name="phone" value={form.phone} onChange={handleChange} placeholder="Telefon" required />
                    <input type="text" name="city" value={form.city} onChange={handleChange} placeholder="Grad" required />
                    <textarea name="message" value={form.message} onChange={handleChange} placeholder="Poruka" />
                    <button type="submit">Pošalji</button>
                </form>
            </div>
        </section>
    );
}
